@import url('https://fonts.googleapis.com/css2?family=Titillium+Web:wght@300&display=swap');


*,
*::after,
*::before {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

$clr-bg-primary: #12263A;
$clr-bg-secondary-light: #23231A;
$clr-text: #fff;
$clr-accent: #FAFF81;
$clr-accent2: #68A175;
$clr-accent-dark: #5B7553;

body {
    width: 100%;
    height: 100vh;
    background: $clr-bg-primary;
    font-family: 'Titillium Web', sans-serif;
    color: #fff;
    display: grid;
    grid-template-columns: 20% 80%;
}
  
button {
    width: 200px;
    height: 50px;
    background: $clr-accent2;
    border: none;
    font-size: .6em;
}
  
button:hover {
    background: $clr-accent-dark;
    cursor: pointer;
    opacity: .9;
}

header {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    background: $clr-bg-primary;
    margin: 2em 4em;
    
    h1 {
        font-size: 3rem;
        margin-bottom: 1em;
    }

    img {
        background: $clr-accent2;
        width: 80%;
        margin: 0 1em;
        border-radius: 40px;
    }
    nav {
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        background-image: $clr-bg-primary;

        .tabs {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            gap: 3em;
            font-size: 2em;
            padding: 1em 2em;
            border-radius: 2em;
            position: relative;

            a {
                text-decoration: none;
                border-bottom: 4px solid purple;
                cursor: pointer;
                transition: opacity 250ms, transform 250ms;
            }

            a:hover {
                border-bottom: 4px solid $clr-accent;
                animation: highlight 250ms forwards;
            }    
        }
    }

}

main {
    display: flex;
    justify-content: center;
    align-content: center;

    .data {

        display: flex;
        justify-content: center;
        align-items: center;
        background: #12263A;
        position: relative;
        margin-right: 2em;
        width: 100%;
        margin: 2em 4em;

        section.standings {
            display: none;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            width: 700px;
            height: 100%;
            position: absolute;
            font-weight: bold;
            gap: .2em;
            visibility: hidden;
            opacity: 0;
            transition: opacity 250ms, visibility 250ms, transform 250ms;

            div {
                display: grid;
                grid-template-columns: .2fr 1fr .2fr .2fr .2fr .2fr .2fr;
                gap: 1em;
                width: 700px;
                color: #000;
                background: $clr-accent2;
                padding: .2em;
                span {
                    text-align: right;
                    font-size: 1.2em;
                }
                span:first-of-type {
                    border-right: 1px solid white;
                    text-align: center;
                }
                span:nth-of-type(2) {
                    border-right: 1px solid white;
                    display: flex;
                    justify-content: flex-start;
                    align-items: center;
                    flex-direction: row;
                    gap: 1em;
                    
                    img {
                        width: 25px;
                        display: inline-block;
                    }
                }
                span:nth-of-type(3), 
                span:nth-of-type(4), 
                span:nth-of-type(5), 
                span:nth-of-type(6), 
                span:nth-of-type(7) {
                    text-align: center;
                }
            }

            div.headings {
                background: #8EB897;
                span {
                    font-size: .9em;
                    text-align: center;
                }
            }
        }
        
        section.standings.is_visible {
            display: flex;
            animation: slideIn 250ms forwards;
        }

        section.fixtures_section {
            color: #000000;
            font-weight: bold;
            text-transform: uppercase;
            display: none;
            grid-template-columns: .2fr 1fr .2fr;
            grid-template-rows: .125fr 1fr;
            gap: 1em;
            font-size: 1em;
            width: 100%;
            opacity: 0;
            visibility: hidden;
            position: absolute;
            grid-template-areas: 
              "prevWeek weekNum nextWeek"
              "prevWeek fixtures nextWeek"
            ;
            transition: opacity 250ms, transform 250ms, visibility 250ms;

            div.fixtures {
                grid-area: fixtures;
                transition: opacity 250ms, transform 250ms cubic-bezier(.33,.15,.55,.86);
                
                div {
                    background: $clr-accent2;
                    display: grid;
                    grid-template-columns: 1fr .2fr 1fr;
                    grid-template-rows: 1fr;
                    grid-template-areas: 
                      "homeTeam score awayTeam"
                      "additionalInfo additionalInfo additionalInfo";

                    span > img {
                            width: 25px;
                            display: inline-block;
                            padding: 5px 0 5px 0;
                          }
                    }

                   span:nth-of-type(1) {
                        grid-area: homeTeam;
                        text-align: center;
                        padding: 5px 0 5px 0;
                        display: flex;
                        justify-content: flex-end;
                        gap: 1em;
                        margin-right: 1em;
                        align-items: center;
                        flex-direction: row;
                   }

                   span:nth-of-type(2) {
                        grid-area: score;
                        text-align: center;
                        background: $clr-accent;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        font-size: 1.2rem;
                  }

                    span:nth-of-type(3) {
                        grid-area: awayTeam;
                        text-align: center;
                        padding: 5px 0 5px 0;
                        display: flex;
                        justify-content: flex-start;
                        margin-left: 1em;
                        gap: 1em;
                        align-items: center;
                        flex-direction: row;
                  }

                    div {
                        grid-area: additionalInfo;
                        display: flex;
                        flex-direction: row;
                        justify-content: center;
                        align-items: center;
                        background: #5B7553;
                        text-align: center;
                        gap: 1em;
                        padding: 5px 0 5px 0;
                        font-size: small;
                  }
                }
            }

            div.fixtures.skipped {
                animation: reappear 250ms;
            }

            div.weekHeadingContainer {
                grid-area: weekNum;
                display: flex;
                justify-content: center;
                align-items: center;
                background: $clr-accent2;
                font-size: 1.5rem;
            }

            div.prevWeekNavCont, div.nextWeekNavCont {
                color: #fff;
                writing-mode: vertical-lr;
                text-orientation: upright;
                display: flex;
                justify-content: center;
                align-items: center;
                cursor: pointer;
                font-size: 1.2em;
                background: $clr-accent2;
                     user-select: none;
                -moz-user-select: none;
                -webkit-user-select: none;
            }

            div.prevWeekNavCont {
                grid-area: prevWeek;
            }

            div.nextWeekNavCont {
                grid-area: nextWeek;
            }

            div.nextWeekNavCont:hover, div.prevWeekNavCont:hover {
                background: $clr-accent;
                color: #000;
            }
        }

        section.fixtures_section.is_visible {
            display: grid;
            animation: slideIn 250ms forwards;
        }

        section.statistics {
            display: none;
            width: 575px;
            height: 100%;
            margin: auto;
            font-size: 1.2em;
            gap: .2em;
            justify-content: center;
            align-items: center;
            flex-direction: column;
            font-weight: bold;
            color: #000;
            transition: opacity 250ms, transform 250ms, visibility 250ms;

            div {
                background: $clr-accent2;
                width: 100%;
                display: grid;
                grid-template-columns: 1fr .3fr .3fr;
                padding: .2em;

                span:nth-of-type(1) {
                    text-indent: 1em;
                }
                span:nth-of-type(2), span:nth-of-type(3) {
                    text-align: center;
                } 
            }

            div:first-child {
                background: #8EB897;
                font-size: .9em;
            }
        }

        section.statistics.is_visible {
            display: flex;
            animation: slideIn 250ms forwards;
        }

}




@keyframes slideIn {
    0% {
      opacity: 0;
      visibility: hidden;
      transform: translateY(-100%);
    }
    100% {
      opacity: 1;
      visibility: visible;
      transform: translateY(0);
    }
}

@keyframes highlight {
    0% {
      opacity: 1;
      transform: scale(1);
    }
    100% {
      opacity: .9;
      transform: scale(.95);
    }
  }

  @keyframes reappear {
    0% {
      opacity: .7;
      transform: translateY(2%);
    }
    100% {
      opacity: 1;
      transform: translateY(0);
    }
  }
